// Generated by Selenium IDE
const { Builder, By, Key, until } = require('selenium-webdriver')
const assert = require('assert')

describe('Searching with the Search Bar', function() {
  this.timeout(30000)
  let driver
  let vars
  beforeEach(async function() {
    driver = await new Builder().forBrowser('chrome').build()
    vars = {}
  })
  afterEach(async function() {
    await driver.quit();
  })
  it('Searching with the Search Bar', async function() {
    await driver.get("https://moviesroom.pl//")
    await driver.manage().window().setRect({ width: 1920, height: 1080 })
    await driver.findElement(By.css(".fa-search")).click()
    await driver.findElement(By.id("s")).click()
    await driver.findElement(By.id("s")).sendKeys("Breaking Bad")
    await driver.findElement(By.id("s")).sendKeys(Key.ENTER)
  })
})
